import logo from './logo.svg';
import './App.css';
import 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css';

import { Col, Button, Row, Container, Card, Form } from "react-bootstrap";
import axios from'axios';
import React,{useState,state} from 'react'
// import {internalIpV6, internalIpV4} from 'internal-ip';
import { ClientJS } from 'clientjs';


import date from 'date-and-time';
function App() {
  const [data, setData] = useState({
    name: "",
    bannerid: "" 

  });
  const [submitted, setSubmitted] = useState(false);
  const handleChange = (e) => {
    setData({ ...data, [e.target.name]: e.target.value });
  };


  const handleSubmit = async (e) => {
    e.preventDefault();
    // console.log(await internalIpV6());


    let latitude = null;
    let longitude = null;

    try {
        const position = await new Promise((resolve, reject) => {
            navigator.geolocation.getCurrentPosition(resolve, reject);
        });
        latitude = position.coords.latitude;
        longitude = position.coords.longitude;
    } catch (error) {
        if (error.code === error.PERMISSION_DENIED) {
            alert("Please allow location access to use this feature.");
            return;
        }
    }


    const client = new ClientJS()
    const sessionid=client.getFingerprint();
    console.log(client.getFingerprint())
    const now = new Date();
    const formattedDate = date.format(now, 'YYYY/MM/DD');
    const formattedTime = date.format(now, 'HH:mm:ss');



    axios.post(`http://localhost:8000`, {...data, sessionid,formattedDate,formattedTime,latitude,longitude}).then((res) => {
        console.log(res);
        console.log(res.data);
        setSubmitted(true);
        if(res=='Location not allowed'){
          alert('Location not allowed')
        }
      });
  };

  return(
    <Container>
    <div className='d-flex justify-content-center align-items-center bg-primary vh-100'>
      <Row className="vh-100 d-flex justify-content-center align-items-center">
        <Col md={8} lg={6} xs={12}>
          <div className="border border-3 border-primary"></div>
          <Card className="shadow">
            <Card.Body>
              <h2 className="fw-bold mb-2 text-uppercase">Computer Networks Attendance</h2>
              {submitted ? (
                  <div className="alert alert-success" role="alert">
                    Form submitted successfully!
                  </div>
                ) : null}
              <div className="mb-3 mt-4">
                <form onSubmit={handleSubmit}>
                  <div className="form-group">
                    <label htmlFor='name'>FullName</label>
                    <input type="text" className="form-control" placeholder="Enter full name" name='name' value={data.name} onChange={handleChange}></input>
                  </div>
                  <div className="form-group">
                    <label htmlFor="bannerid">Banner ID</label>
                    <input type="text" className="form-control" placeholder="Enter your banner id" name='bannerid' value={data.bannerid} onChange={handleChange}></input>
                  </div>
                  <button className='btn btn-primary' type='submit' onClick={onclick}>Submit</button>
                </form>
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </div>
  </Container>
)
}

export default App;
