import logo from './logo.svg';
import './App.css';
import 'react-bootstrap'
import { Col, Button, Row, Container, Card, Form } from "react-bootstrap";
import axios from'axios';
import React,{useState,state} from 'react'
// import {internalIpV6, internalIpV4} from 'internal-ip';
import { ClientJS } from 'clientjs';

import { useEffect } from 'react';
import { v4 as uuidv4 } from 'uuid';
import Cookies from 'js-cookie';
import date from 'date-and-time';
function App() {
  const [data, setData] = useState({
    name: "",
    bannerid: "" 

  });
  const handleChange = (e) => {
    setData({ ...data, [e.target.name]: e.target.value });
  };


  const [userId, setUserId] = useState('');
  useEffect(() => {
    const existingUserId = Cookies.get('userId');
    if (!existingUserId) {
      const newUserId = uuidv4(); // Generate a new UUID
      Cookies.set('userId', newUserId, { expires: 365 }); // Set the cookie to expire in 1 year
      setUserId(newUserId);
    } else {
      setUserId(existingUserId);
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    // console.log(await internalIpV6());
    const client = new ClientJS()
    const sessionid=client.getFingerprint();
    console.log(client.getFingerprint())
    const now = new Date();
    const formattedDate = date.format(now, 'YYYY/MM/DD');
    const formattedTime = date.format(now, 'HH:mm:ss');
    axios.post(`http://localhost:8000`, {...data, sessionid,formattedDate,formattedTime}).then((res) => {
        console.log(res);
        console.log(res.data);
      });
  };
  return(
    <Container>
      <div><h1>user:id:{userId}</h1></div>
      <div className='d-flex justify-content-center align-items-center gb-primary vh-100'>
    <Row className="vh-100 d-flex justify-content-center align-items-center">
      <Col md={8} lg={6} xs={12}>
        <div className="border border-3 border-primary"></div>
        <Card className="shadow">
          <Card.Body>
          <h2 className="fw-bold mb-2 text-uppercase">Computer Networks Attendance</h2>
            <div className="mb-3 mt-4">
            <form onSubmit={handleSubmit} >
          <div className="name">
            <label htmlFor='name'>FullName </label>
            <input type="text" placeholder="enter full name"  name='name' value={data.name} onChange={handleChange} ></input>
          </div>
          <div className="bannerid">
            <label htmlFor="bannerid">Banner ID </label>
            <input type="text" placeholder=" enter your banner id"  name='bannerid' value={data.bannerid} onChange={handleChange} ></input>
          </div>
          <button className='Button' type='submit'  >Submit</button> 
        </form>
        </div>
        </Card.Body>
        </Card>
        </Col>
        </Row>
        </div>
        </Container>
)
}

export default App;
