import logo from './logo.svg';
import './App.css';
import 'react-bootstrap'
import { Col, Button, Row, Container, Card, Form } from "react-bootstrap";
import axios from'axios';



function App() {
  const [data, setData] = useState({
    name: "",
    bannerid: ""
  });
  const handleChange = (e) => {
    const value = e.target.value;
    setData({
      ...data,
      [e.target.name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const userData = {
      name: data.name,
      bannerid: data.bannerid
    };
    axios.post("https://localhost:8000/post", userData).then((response) => {
      console.log(response.status, response.data.token);
    });
  };
  return(
    <Container>
      <div className='d-flex justify-content-center align-items-center gb-primary vh-100'>
    <Row className="vh-100 d-flex justify-content-center align-items-center">
      <Col md={8} lg={6} xs={12}>
        <div className="border border-3 border-primary"></div>
        <Card className="shadow">
          <Card.Body>
          <h2 className="fw-bold mb-2 text-uppercase">Computer Networks Attendance</h2>
            <div className="mb-3 mt-4">
            <form >
          <div className="name">
            <label htmlFor='name'>FullName </label>
            <input type="text" placeholder="enter full name" value={state.name} onChange={handleChange} ></input>
          </div>
          <div className="bannerid">
            <label htmlFor="bannerid">Banner ID </label>
            <input type="text" placeholder=" enter your banner id" value={state.bannerid} onChange={handleChange} ></input>
          </div>
          <button className='button' type='submit'  >Submit</button> 
        </form>
        </div>
        </Card.Body>
        </Card>
        </Col>
        </Row>
        </div>
        </Container>
)
}

export default App;
